mapper.select:
select count(*) from board a, users b where a.userId = b.id and a.commentIndent = 0
and a.title like '%' || #{keyword} || '%'
and a.userId = #{userId}

select count(*) from board a, users b where a.userId = b.id and a.commentIndent = 0 and a.boardType = 1
and a.title like '%' || #{keyword} || '%'
and a.userId = #{userId}

select *
from (
select rownum rn, c.*
from (
select a.id, a.title, a.content, b.name, a.createdAt, a.updatedAt,
a.readCount,  a.status
from board a, users b
where a.userId = b.id
and a.boardType = 1

    and a.status = #{status}


    and a.title like '%' || #{keyword} || '%'

) c
)
where rn between #{start} and #{end}
