mapper.select:
select count(*) from board a, users b where a.userId = b.id and a.commentIndent = 0
and a.title like '%' || #{keyword} || '%'
and a.userId = #{userId}

mapper.update:
UPDATE board
SET title = #{title}, content = #{content}, status = #{status}, fileName = #{fileName} ,
    fileSize =  #{fileSize},  filePath = #{filePath}, createdAt = #{createdAt}, isPinned = #{isPinned}, fileAddress = #{fileAddress}
WHERE id = #{id}

UPDATE board
SET title = #{title}, content = #{content}, status = #{status}
WHERE id = #{id}

UPDATE board
SET title = #{title}, content = #{content}, status = #{status}
WHERE id = #{id}

update board
set commentStep = commentStep + 1
where commentGroupId = #{commentGroupId}
  and commentStep > #{commentStep}
