mapper.select:
SELECT p.id, p.userId, p.contentId, p.paymentType, p.status, p.purchaseDate, p.price, u.name, g.title FROM payments p, users u, gamecontents g
WHERE userId = ( SELECT id FROM users WHERE name like '%'|| #{keyword} || '%' AND id = userId ) AND purchaseDate BETWEEN #{keywordDate1} and #{keywordDate2}
WHERE contentId = ( SELECT id FROM gamecontents WHERE title like '%'|| #{keyword} || '%' AND id = contentId ) AND purchaseDate BETWEEN #{keywordDate1} and #{keywordDate2}
WHERE userId = ( SELECT id FROM users WHERE name like '%'|| #{keyword} || '%' AND id = userId )
WHERE contentId = ( SELECT id FROM gamecontents WHERE title like '%'|| #{keyword} || '%' AND id = contentId )
WHERE userId = ( SELECT id FROM users WHERE name like '%'|| #{keyword} || '%' AND id = userId ) AND purchaseDate >= #{keywordDate1}
WHERE contentId = ( SELECT id FROM gamecontents WHERE title like '%'|| #{keyword} || '%' AND id = contentId ) AND purchaseDate >= #{keywordDate1}
WHERE userId = ( SELECT id FROM users WHERE name like '%'|| #{keyword} || '%' AND id = userId ) AND purchaseDate <= #{keywordDate2}
WHERE contentId = ( SELECT id FROM gamecontents WHERE title like '%'|| #{keyword} || '%' AND id = contentId ) AND purchaseDate <= #{keywordDate2}
WHERE purchaseDate >= #{keywordDate1}
WHERE purchaseDate <= #{keywordDate2}
WHERE purchaseDate BETWEEN #{keywordDate1} and #{keywordDate2}
AND p.contentId = g.id AND p.userid = u.id
