mapper.select:
SELECT   p.id, p.userId, p.contentId, p.paymentType, p.status, p.purchaseDate, p.price, u.name, g.title
FROM     payments p, users u, gamecontents g
ORDER BY p.id desc



            WHERE u.name like '%'|| #{keyword} || '%'
            AND purchaseDate BETWEEN #{keywordDate1} and #{keywordDate2}


            WHERE g.title like '%'|| #{keyword} || '%'
            AND purchaseDate BETWEEN #{keywordDate1} and #{keywordDate2}




            WHERE u.name like '%'|| #{keyword} || '%'


            WHERE g.title like '%'|| #{keyword} || '%'





            WHERE u.name like '%'|| #{keyword} || '%'
            AND purchaseDate  >=  #{keywordDate1}


            WHERE g.title like '%'|| #{keyword} || '%'
            AND purchaseDate  >=  #{keywordDate1}




            WHERE u.name like '%'|| #{keyword} || '%'
            AND purchaseDate  <=  #{keywordDate2}


            WHERE g.title like '%'|| #{keyword} || '%'
            AND purchaseDate  <=  #{keywordDate2}



        WHERE purchaseDate  >=  #{keywordDate1}


        WHERE purchaseDate  <=  #{keywordDate2}


        WHERE purchaseDate BETWEEN #{keywordDate1} and #{keywordDate2}


AND    p.contentId = g.id
AND    p.userid = u.id
